// ResumeForm.js

import React, { useState } from 'react';
import SkillForm from './SkillForm';
import SkillData from './SkillData';
import ProjectForm from './ProjectForm';
import ProjectData from './ProjectData';

function ResumeForm({ resumeId }) {
  const [isSkillActive, setIsSkillActive] = useState(false);
  const [isProjectActive, setIsProjectActive] = useState(false);

  return (
    <div className="ResumeForm" style={{ textAlign: 'center' }}>
      <h1>Resume Form</h1>
      <div>
        <button style={{ backgroundColor: isSkillActive ? 'green' : 'transparent' }} onClick={() => setIsSkillActive(!isSkillActive)}>
          SKILL
        </button>
        <button style={{ backgroundColor: isProjectActive ? 'green' : 'transparent' }} onClick={() => setIsProjectActive(!isProjectActive)}>
          PROJECT
        </button>
      </div>
      {isSkillActive && (
        <>
          <SkillForm resumeId={resumeId} />
          <SkillData resumeId={resumeId} />
        </>
      )}
      {isProjectActive && (
        <>
          <ProjectForm resumeId={resumeId} />
          <ProjectData resumeId={resumeId} />
        </>
      )}
    </div>
  );
}

export default ResumeForm;
