// ProjectData.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProjectData({ resumeId }) {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    if (resumeId) {
      fetchProjects();
    }
  }, [resumeId]);

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`/api/resumes/${resumeId}/projects`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const handleDeleteProject = async (id) => {
    try {
      await axios.delete(`/api/resumes/${resumeId}/projects/${id}`);
      fetchProjects();
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  return (
    <div>
      <h3>PROJECT 조회</h3>
      <ul>
        {projects.map((project) => (
          <li key={project.id}>
            <div>
              <strong>프로젝트명:</strong> {project.project_title}
            </div>
            <div>
              <strong>부연설명:</strong> {project.project_content}
            </div>
            <div>
              <strong>프로젝트기간:</strong> {project.project_period_start} ~ {project.is_in_progress ? '진행중' : project.project_period_end}
            </div>
            <button onClick={() => handleDeleteProject(project.id)}>X</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProjectData;
