// ResumeFormProject.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ResumeFormProject({ resumeId }) {
  const [projectTitle, setProjectTitle] = useState('');
  const [projectContent, setProjectContent] = useState('');
  const [projectPeriod, setProjectPeriod] = useState('');
  const [isCurrent, setIsCurrent] = useState(false);
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    fetchProjects();
  }, [resumeId]);

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`/api/resumes/${resumeId}/projects`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const handleProjectSubmit = async () => {
    try {
      await axios.post(`/api/resumes/${resumeId}/projects`, {
        project_title: projectTitle,
        project_content: projectContent,
        project_period: projectPeriod,
        is_current: isCurrent
      });
      setProjectTitle('');
      setProjectContent('');
      setProjectPeriod('');
      setIsCurrent(false);
      fetchProjects();
    } catch (error) {
      console.error('Error submitting project:', error);
    }
  };

  return (
    <div>
      <h2>PROJECT</h2>
      <div>
        <label>
          프로젝트명:
          <input type="text" value={projectTitle} onChange={(e) => setProjectTitle(e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          부연설명:
          <input type="text" value={projectContent} onChange={(e) => setProjectContent(e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          프로젝트기간:
          <input type="text" value={projectPeriod} onChange={(e) => setProjectPeriod(e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          진행여부:
          <input type="checkbox" checked={isCurrent} onChange={(e) => setIsCurrent(e.target.checked)} /> 진행중
        </label>
      </div>
      <button onClick={handleProjectSubmit}>+</button>
      <h3>PROJECT 조회</h3>
      <ul>
        {projects.map((project) => (
          <li key={project.id}>
            <div>
              <strong>프로젝트명:</strong> {project.project_title}
            </div>
            <div>
              <strong>부연설명:</strong> {project.project_content}
            </div>
            <div>
              <strong>프로젝트기간:</strong> {project.project_period}
            </div>
            <div>
              <strong>진행여부:</strong> {project.is_current ? '진행중' : '완료'}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ResumeFormProject;
