// ResumeFormSkill.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ResumeFormSkill({ resumeId }) {
  const [skillTitle, setSkillTitle] = useState('');
  const [skillDescription, setSkillDescription] = useState('');

  const handleCreateSkill = async () => {
    try {
      await axios.post(`/api/resumes/${resumeId}/skills`, {
        skill_title: skillTitle,
        skill_content: skillDescription
      });
      setSkillTitle('');
      setSkillDescription('');
      // Trigger fetching of skills to update the list
      // You can implement this if necessary
    } catch (error) {
      console.error('Error creating skill:', error);
    }
  };

  return (
    <div className="ResumeFormSkill" style={{ textAlign: 'center' }}>
      <h2>SKILL 입력 폼</h2>
      <form onSubmit={handleCreateSkill}>
        <div>
          <label>기술명 : </label>
          <input type="text" value={skillTitle} onChange={(e) => setSkillTitle(e.target.value)} />
        </div>
        <div>
          <label>부연설명 : </label>
          <textarea value={skillDescription} onChange={(e) => setSkillDescription(e.target.value)} />
        </div>
        <button type="submit">+</button>
      </form>
    </div>
  );
}

export default ResumeFormSkill;
