// ResumeFormSkill.js

import React from 'react';
import axios from 'axios';

function ResumeFormSkill({ resumeId }) {
  const [skills, setSkills] = React.useState([]);

  React.useEffect(() => {
    fetchSkills();
  }, [resumeId]);

  const fetchSkills = async () => {
    try {
      const response = await axios.get(`/api/resumes/${resumeId}/skills`);
      setSkills(response.data);
    } catch (error) {
      console.error('Error fetching skills:', error);
    }
  };

  const deleteSkill = async (id) => {
    try {
      await axios.delete(`/api/resumes/${resumeId}/skills/${id}`);
      fetchSkills();
    } catch (error) {
      console.error('Failed to delete skill', error);
    }
  };

  return (
    <div>
      <h2>Skills</h2>
      {skills.length > 0 ? (
        skills.map(skill => (
          <div key={skill.id}>
            <h3>스킬명: {skill.skill_title}</h3>
            <p>부연설명: {skill.skill_content}</p>
            <button onClick={() => deleteSkill(skill.id)}>X</button>
          </div>
        ))
      ) : (
        <p>No skills found.</p>
      )}
    </div>
  );
}

export default ResumeFormSkill;
