// ResumeForm.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ResumeFormSkill from './ResumeFormSkill';
import ResumeFormProject from './ResumeFormProject';

function ResumeForm() {
  const { resume_id } = useParams();
  const [activeSection, setActiveSection] = useState(null);

  return (
    <div className="ResumeForm" style={{ textAlign: 'center' }}>
      <h1>Resume Form</h1>
      <div>
        <button onClick={() => setActiveSection('skill')}>SKILL</button>
        <button onClick={() => setActiveSection('project')}>PROJECT</button>
      </div>
      {activeSection === 'skill' && <ResumeFormSkill resumeId={resume_id} />}
      {activeSection === 'project' && <ResumeFormProject resumeId={resume_id} />}
    </div>
  );
}

export default ResumeForm;
