// ResumeFormSkill.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ResumeFormSkill({ resumeId }) {
  const [skillTitle, setSkillTitle] = useState('');
  const [skillContent, setSkillContent] = useState('');
  const [skills, setSkills] = useState([]);

  useEffect(() => {
    fetchSkills();
  }, [resumeId]);

  const fetchSkills = async () => {
    try {
      const response = await axios.get(`/api/resumes/${resumeId}/skills`);
      setSkills(response.data);
    } catch (error) {
      console.error('Error fetching skills:', error);
    }
  };

  const handleSkillSubmit = async () => {
    try {
      await axios.post(`/api/resumes/${resumeId}/skills`, {
        skill_title: skillTitle,
        skill_content: skillContent
      });
      // Clear input fields after successful submission
      setSkillTitle('');
      setSkillContent('');
      // Refetch skills to update the list immediately
      fetchSkills();
    } catch (error) {
      console.error('Error submitting skill:', error);
    }
  };

  const handleDeleteSkill = async (id) => {
    try {
      await axios.delete(`/api/resumes/${resumeId}/skills/${id}`);
      // Refetch skills to update the list immediately
      fetchSkills();
    } catch (error) {
      console.error('Error deleting skill:', error);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Add New Skill</h2>
      <div>
        <label>
          Skill Title:
          <input type="text" value={skillTitle} onChange={(e) => setSkillTitle(e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          Skill Content:
          <textarea value={skillContent} onChange={(e) => setSkillContent(e.target.value)} />
        </label>
      </div>
      <button onClick={handleSkillSubmit}>Submit</button>
      <h2>Skills</h2>
      <ul>
        {skills.map((skill) => (
          <li key={skill.id}>
            <div>
              <strong>Skill Title:</strong> {skill.skill_title}
            </div>
            <div>
              <strong>Skill Content:</strong> {skill.skill_content}
            </div>
            <button onClick={() => handleDeleteSkill(skill.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ResumeFormSkill;
