// ResumeFormProject.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ResumeFormProject({ resumeId }) {
  const [projectTitle, setProjectTitle] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [projectStartDate, setProjectStartDate] = useState('');
  const [projectEndDate, setProjectEndDate] = useState('');
  const [isProjectInProgress, setIsProjectInProgress] = useState(false);

  const handleCreateProject = async () => {
    try {
      await axios.post(`/api/resumes/${resumeId}/projects`, {
        project_title: projectTitle,
        project_content: projectDescription,
        project_period: `${projectStartDate} ~ ${projectEndDate}`,
        is_current: isProjectInProgress
      });
      setProjectTitle('');
      setProjectDescription('');
      setProjectStartDate('');
      setProjectEndDate('');
      setIsProjectInProgress(false);
      // Trigger fetching of projects to update the list
      // You can implement this if necessary
    } catch (error) {
      console.error('Error creating project:', error);
    }
  };

  return (
    <div className="ResumeFormProject" style={{ textAlign: 'center' }}>
      <h2>PROJECT 입력 폼</h2>
      <form onSubmit={handleCreateProject}>
        <div>
          <label>프로젝트명 : </label>
          <input type="text" value={projectTitle} onChange={(e) => setProjectTitle(e.target.value)} />
        </div>
        <div>
          <label>부연설명 : </label>
          <textarea value={projectDescription} onChange={(e) => setProjectDescription(e.target.value)} />
        </div>
        <div>
          <label>프로젝트기간 : </label>
          <input type="text" value={projectStartDate} onChange={(e) => setProjectStartDate(e.target.value)} /> ~
          <input type="text" value={projectEndDate} onChange={(e) => setProjectEndDate(e.target.value)} />
        </div>
        <div>
          <label>진행여부 : </label>
          <select value={isProjectInProgress} onChange={(e) => setIsProjectInProgress(e.target.value)}>
            <option value={true}>진행중</option>
            <option value={false}>완료</option>
          </select>
        </div>
        <button type="submit">+</button>
      </form>
    </div>
  );
}

export default ResumeFormProject;
