// ResumeFormProject.js

import React from 'react';
import axios from 'axios';

function ResumeFormProject({ resumeId }) {
  const [projects, setProjects] = React.useState([]);

  React.useEffect(() => {
    fetchProjects();
  }, [resumeId]);

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`/api/resumes/${resumeId}/projects`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const deleteProject = async (id) => {
    try {
      await axios.delete(`/api/resumes/${resumeId}/projects/${id}`);
      fetchProjects();
    } catch (error) {
      console.error('Failed to delete project', error);
    }
  };

  return (
    <div>
      <h2>Projects</h2>
      {projects.length > 0 ? (
        projects.map(project => (
          <div key={project.id}>
            <h3>프로젝트명: {project.project_title}</h3>
            <p>부연설명: {project.project_content}</p>
            <p>제작기간: {project.project_period}</p>
            <p>진행여부: {project.is_current ? '진행중' : '완료'}</p>
            <button onClick={() => deleteProject(project.id)}>X</button>
          </div>
        ))
      ) : (
        <p>No projects found.</p>
      )}
    </div>
  );
}

export default ResumeFormProject;
