// ResumeForm.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ResumeFormSkill from './ResumeFormSkill';
import ResumeFormProject from './ResumeFormProject';

function ResumeForm() {
  const { resume_id } = useParams();
  const [resume, setResume] = useState(null);
  const [activeForm, setActiveForm] = useState(null);

  useEffect(() => {
    fetchResume();
  }, [resume_id]);

  const fetchResume = async () => {
    try {
      const response = await axios.get(`/api/resumes/${resume_id}`);
      setResume(response.data);
    } catch (error) {
      console.error('Error fetching resume:', error);
    }
  };

  const handleFormActivation = (form) => {
    setActiveForm(form);
  };

  return (
    <div className="ResumeForm">
      <h1>Resume Form</h1>
      {resume && (
        <div>
          {/* Display resume content */}
        </div>
      )}
      <div>
        <button onClick={() => handleFormActivation('skill')}>Skill</button>
        <button onClick={() => handleFormActivation('project')}>Project</button>
      </div>
      {activeForm === 'skill' && <ResumeFormSkill resumeId={resume_id} />}
      {activeForm === 'project' && <ResumeFormProject resumeId={resume_id} />}
    </div>
  );
}

export default ResumeForm;
