// SkillData.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SkillData({ resumeId }) {
  const [skills, setSkills] = useState([]);

  useEffect(() => {
    if (resumeId) {
      fetchSkills();
    }
  }, [resumeId]);

  const fetchSkills = async () => {
    try {
      const response = await axios.get(`/api/resumes/${resumeId}/skills`);
      setSkills(response.data);
    } catch (error) {
      console.error('Error fetching skills:', error);
    }
  };

  const handleDeleteSkill = async (id) => {
    try {
      await axios.delete(`/api/resumes/${resumeId}/skills/${id}`);
      fetchSkills();
    } catch (error) {
      console.error('Error deleting skill:', error);
    }
  };

  return (
    <div>
      <h3>SKILL 조회</h3>
      <ul>
        {skills.map((skill) => (
          <li key={skill.id}>
            <div>
              <strong>기술명:</strong> {skill.skill_title}
            </div>
            <div>
              <strong>부연설명:</strong> {skill.skill_content}
            </div>
            <button onClick={() => handleDeleteSkill(skill.id)}>X</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SkillData;
