// ResumeForm.js

import React, { useState } from 'react';
import SkillForm from './SkillForm';
import SkillData from './SkillData';
import ProjectForm from './ProjectForm';
import ProjectData from './ProjectData';

function ResumeForm({ resumeId }) {
  const [activeTab, setActiveTab] = useState(null);

  return (
    <div className="ResumeForm" style={{ textAlign: 'center' }}>
      <h1>Resume Form</h1>
      <div>
        <button style={{ backgroundColor: activeTab === 'skill' ? 'green' : 'transparent' }} onClick={() => setActiveTab(activeTab === 'skill' ? null : 'skill')}>
          SKILL
        </button>
        <button style={{ backgroundColor: activeTab === 'project' ? 'green' : 'transparent' }} onClick={() => setActiveTab(activeTab === 'project' ? null : 'project')}>
          PROJECT
        </button>
      </div>
      {activeTab === 'skill' && (
        <>
          <SkillForm resumeId={resumeId} />
          <SkillData resumeId={resumeId} />
        </>
      )}
      {activeTab === 'project' && (
        <>
          <ProjectForm resumeId={resumeId} />
          <ProjectData resumeId={resumeId} />
        </>
      )}
    </div>
  );
}

export default ResumeForm;
